{panel:title=Alterar a classe SalarySurveyBaseDataProcessor para que preencha os valores desses respectivos campos com o valor presente na SalarySurveyDataItem.}

{code:java}
        public class SalarySurveyBaseDataProcessor implements DataProcessor<SalarySurveyDataItem, SalarySurveyDataEntity> {

    public static final String LOG_SALARYSURVEY_DATALOAD_PROCESSING = "Processando dados base da pesquisa salarial";
    private final ApplicationContext applicationContext;

    public SalarySurveyBaseDataProcessor(ApplicationContext applicationContext) {
        this.applicationContext = applicationContext;
    }

    @Override
    public void process(SalarySurveyDataItem data, SalarySurveyDataEntity entity, Map<String, Set<KeyPair<String, String>>> loadContext) {

        log.info(LOG_SALARYSURVEY_DATALOAD_PROCESSING);
        entity.setBaseSalary(data.getBasesalary());
        entity.setCnae(data.getCnae());
        entity.setBranchOffice(data.getBranchoffice());
        entity.setAdditionalFunctionPercentage(data.getAdditionalfunctionpercentage());
        entity.setAdditionalFunctionValue(data.getAdditionalfunctionvalue());
        entity.setCompetence(data.getCompetence());
        entity.setCompany(data.getCompany());
        entity.setContractType(data.getContracttype());
        entity.setHazardPercentage(data.getHazardpercentage());
        entity.setHazardValue(data.getHazardvalue());
        entity.setHireDate(LocalDate.of(data.getHiredate_year(), data.getHiredate_month(), data.getHiredate_day()));
        entity.setInsalubrityPercentage(data.getInsalubritypercentage());
        entity.setInsalubrityValue(data.getInsalubrityvalue());
        entity.setJobPosition(data.getJobposition());
        entity.setRegionalMinimumSalary(data.getRegionalminimumsalary());
        entity.setSocialMinimumSalary(data.getSocialminimumsalary());
        entity.setSpendingMoney(data.getSpendingmoney());
        entity.setState(data.getState());
        entity.setWorkShift(Long.valueOf(data.getWorkshift()));
        entity.setSourceId(data.getId());

        entity.setJobPositionCategory(data.getJobtitlenormalized());
        entity.setJobPositionDescription(data.getJobpositiondescription());
        entity.setCboCode(data.getCbo());
        entity.setCboName(data.getCbodescription());
        entity.setJobPositionLabel(data.getJobtitlelabel() != null ? Long.valueOf(data.getJobtitlelabel()) : 0L);
        entity.setSeniorityName(data.getSeniority());
        entity.setSegmentName(data.getCompanysegment());

        entity.setCategoryJobPositionLabel(data.getCategoryjobposition() != null ? Long.valueOf(data.getCategoryjobposition()) : 0L);
    }

    @Override
    public ApplicationContext getApplicationContext() {
        return applicationContext;
    }
}

    }


