Pra fazer a identificação global de todos os tenants, pode ser executado esse script
{code:sql}
DO
$$
DECLARE
    rec          record;
    cnt1          bigint;
BEGIN
    /* Cabeçalho */
    RAISE INFO 'tenant	ocorrencias';

    FOR rec IN		
		SELECT tx.id FROM   public.tenant tx order by id 
    LOOP        

        BEGIN
            /* muda o schema só nesta iteração */
            EXECUTE format('SET LOCAL search_path TO %I', rec.id);


            EXECUTE $qry$
                select count(*) from (
					select distinct employee from historical_workstation h
					JOIN actingtype a ON h.acting_type = a.id and a.mainhistory is true 
                                        join employee e on e.id = h.employee and (e.dismissalDate is null or e.dismissaldate > now())
					where date_when <= current_date and (end_date IS NULL OR end_date > CURRENT_DATE)
					group by employee 
					having count(*)>1
				) as c;				
            $qry$
            INTO cnt1;

			IF cnt1 > 0 THEN
                RAISE INFO '%	%', rec.id, cnt1;
            END IF;

        EXCEPTION WHEN OTHERS THEN
            /* linha de erro: seq;tenant;;ERROR: ... */
            RAISE INFO '%	ERROR: %', rec.id, SQLERRM;
        END;
    END LOOP;
END;
$$ LANGUAGE plpgsql;
{code}

